import React, { Component } from 'react';
import './App.css';
import TodoList from './TodoList';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      toggled: false, 
    };
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(){
    this.setState({ background: this.state.toggled ? 'rgb(46, 45, 45)' : 'white' })
  }

  render(){
  return (
    <div className="App" style={{backgroundColor: this.state.background}}>
      <div className="App-toggle">
        <label className="switch" for="checkbox">
        <input type="checkbox" id="checkbox" defaultChecked={this.state.toggled} onChange={this.handleChange}/>
        <div className="slider round"></div>
        </label>
      </div>
      <TodoList />
    </div>
  );
  }
}

export default App;
