import React, { Component } from 'react';
import './App.css';
import TodoList from './TodoList';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      background: 'rgb(32, 32, 32)'
    };
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(){
    this.setState({ background: this.state.background === 'white' ? 'rgb(46, 45, 45)' : 'white' })
  }

  render(){
  return (
    <div className="App" style={{backgroundColor: this.state.background}}>
      <button onClick={this.handleClick}>Dark</button>
      <TodoList />
    </div>
  );
  }
}

export default App;
