import React, { Component } from 'react';
import './Todo.css';

class Todo extends Component {
    constructor(props){
        super(props);
        this.state = {
            isEditing: false,
            task: this.props.task
        }
        this.handleRemove = this.handleRemove.bind(this);
        this.toggleForm = this.toggleForm.bind(this);
        this.handleUpdate = this.handleUpdate.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleToggle = this.handleToggle.bind(this);
    }

    handleRemove(){
        this.props.removeTodo(this.props.id);
    }

    toggleForm(){
        this.setState({ isEditing: !this.state.isEditing });
    }

    handleUpdate(e){
        e.preventDefault();
        this.props.updateTodo(this.props.id, this.state.task);
        this.setState({ isEditing: false })
    }

    handleChange(e){
        this.setState({
            task: e.target.value
        })
    }

    handleToggle(){
        this.props.toggleTodo(this.props.id);
    }

    render(){
        let result;
        if (this.state.isEditing){
            result = (<div className="Todo-edit-form">
                <form onSubmit={this.handleUpdate} value={this.state.task} name="task" onChange={this.handleChange}>
                    <input type="text" />
                    <button>Save</button>
                </form>
            </div>)
        } else {
            result = (<div className="Todo">
            <li className={this.props.completed ? "completed" : ""} onClick={this.handleToggle}>{this.props.task}</li>
            <button onClick={this.toggleForm}>Edit</button>
            <button onClick={this.handleRemove}>Done</button>
        </div>)
        }
        return result
    }
}

export default Todo;
